    private void LoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_LoginActionPerformed
        rs = db.querySelectAll("personnel","identifiant='" + User.getText() + "' and mot_de_passe='" + Password.getText()+"'" );
        String username="",psw="",role="";
        int id=0;
        try{
            while(rs.next()){
                username = rs.getString("identifiant");
                psw = rs.getString("mot_de_passe");
                role = rs.getString("role");
                id = Integer.parseInt(rs.getString("id"));
            }
        }
        catch(SQLException e) {e.printStackTrace();}
        if(User.getText().equals("")){
            JOptionPane.showMessageDialog(this, "Veuillez entrer votre identifiant");
        }
        else if(Password.getText().equals("")){
            JOptionPane.showMessageDialog(this, "Veuillez entrer votre mot de passe");
        }
        else if(username.equals(User.getText()) && psw.equals(Password.getText())){
            if(role.equals("admin")){
                new Admin().setVisible(true);
                this.dispose();
            }
            else{
                Caissier caissier = new Caissier(id);
                caissier.setVisible(true);
                this.dispose();
            }
        }
        else{
            JOptionPane.showMessageDialog(this, "le nom utilisateur ou le mots de pass est incorrect");
        }
    }//GEN-LAST:event_LoginActionPerformed

    private void VoirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_VoirActionPerformed
        if(Voir.isSelected()){
            Password.setEchoChar((char) 0);
        }
        else{
            Password.setEchoChar('*');
        }
    }//GEN-LAST:event_VoirActionPerformed

